import React, { useState } from "react";

const Create = () => {
  // const [name, setName] = useState("");
  // const [author, setAuthor] = useState("");
  // const [body, setBody] = useState("");

  const [data, setData] = useState({
    name: "",
    author: "",
    body: "",
  });

  const handle = (e) => {
    const newData = { ...data };
    newData[e.target.id] = e.target.value;
    setData(newData);

    // console.log(newData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let res = await fetch("https://httpbin.org/post", {
        method: "POST",
        body: JSON.stringify({
          name: data.name,
          author: data.author,
          body: data.body,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        console.log("success");
      } else {
        console.log("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }

    // const response = await fetch("/blogs", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify({
    //     name: data.name,
    //     author: data.author,
    //     body: data.body,
    //   }),
    // });
    // const dd = await response.json();

    // if (dd.status === 422 || !dd) {
    //   console.log("invalid");
    // } else {
    //   console.log("success");
    // }
  };

  return (
    <div className="container">
      <div className="create">
        <form>
          <label>Blog Title:</label>
          <input
            id="name"
            type="text"
            value={data.name}
            onChange={(e) => handle(e)}
          />
          <label>Author:</label>
          <input
            id="author"
            type="text"
            value={data.author}
            onChange={(e) => handle(e)}
          />
          <label>Blog Body:</label>
          <textarea
            id="body"
            value={data.body}
            onChange={(e) => handle(e)}
            // required
          />
          <button onClick={handleSubmit}>Add Blog</button>
        </form>
      </div>
    </div>
  );
};

export default Create;
