import React, { useState } from "react";

const Create = () => {
  // const [name, setName] = useState("");
  // const [author, setAuthor] = useState("");
  // const [body, setBody] = useState("");

  const [data, setData] = useState({
    name: "",
    author: "",
    body: "",
  });

  const handle = (e) => {
    // e.preventDefault();

    const newData = { ...data };
    newData[e.target.id] = e.target.value;
    setData(newData);

    // console.log(newData);

    // try {
    //   let res = await fetch("/blogs", requestData);
    //   let resJson = await res.json();
    //   if (res.status === 200) {
    //     setName("");
    //     setAuthor("");
    //     setBody("");
    //   }
    // } catch (err) {
    //   console.log(err);
    // }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const requestData = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: data.name,
        author: data.author,
        body: data.body,
      }),
    };

    // const response = await fetch("/blogs", requestData);
    // await response
    //   .json()
    //   .then((res) => {
    //     console.log(res);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });

    fetch("/blogs", requestData)
      .then((res) => {
        console.log(res);
        return res.json();
      })
      .then((data) => {
        console.log(data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="container">
      <div className="create">
        <form onSubmit={handleSubmit}>
          <label>Blog Title:</label>
          <input
            id="name"
            type="text"
            value={data.name}
            onChange={(e) => handle(e)}
          />
          <label>Author:</label>
          <input
            id="author"
            type="text"
            value={data.author}
            onChange={(e) => handle(e)}
          />
          <label>Blog Body:</label>
          <textarea
            id="body"
            value={data.body}
            onChange={(e) => handle(e)}
            // required
          />
          <button type="submit">Add Blog</button>
        </form>
      </div>
    </div>
  );
};

export default Create;
