  const [data, setData] = useState({
    name: "",
    author: "",
    body: "",
  });

  const handleClick = (e) => {
    // e.preventDefault();

    const newData = { ...data };
    newData[e.target.id] = e.target.value;
    setData(newData);

    console.log(newData);

