  };

  return (
    <div className="container">
      <div className="create">
        <form onSubmit={handleClick}>
          <label>Blog Title:</label>
          <input
            type="text"
            value={data.name}
            onChange={(e) => handleClick(e.target.value)}
          />
          <label>Author:</label>
          <input
            type="text"
