import React, { useEffect, useState } from "react";
import BlogList from "../Components/BlogList";

const Home = () => {
  // const [blogs, setBlogs] = useState([
  //   { title: "Blog Title 1", author: "mario", id: 1 },
  //   { title: "Blog Title 2", author: "yoshi", id: 2 },
  //   { title: "Blog Title 3", author: "ninja", id: 3 },
  //   { title: "Blog Title 4", author: "mario", id: 4 },
  // ]);

  const [blogs, setBlogs] = useState(null);

  useEffect(() => {
    fetch("/all-blogs")
      .then((result) => {
        return result.json();
      })
      .then((data) => {
        // console.log(data);
        setBlogs(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="container">
      <BlogList title="All Blogs!" blogs={blogs && blogs} />
      {blogs &&
        blogs.map((index) => (
          <div className="border">
            <div key={index.id} className="blog-preview">
              <h1>{index.title}</h1>
              <p>Written by {index.author}</p>
            </div>
          </div>
        ))}
    </div>
  );
};

export default Home;
