import React, { useState } from "react";
// import { useNavigate } from "react-router-dom";

const Create = () => {
  // let navigate = useNavigate();

  // const [name, setName] = useState("");
  // const [author, setAuthor] = useState("");
  // const [body, setBody] = useState("");

  const [data, setData] = useState({
    name: "",
    author: "",
    body: "",
  });

  const handle = (e) => {
    const newData = { ...data };
    newData[e.target.id] = e.target.value;
    setData(newData);

    // console.log(newData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let res = await fetch("/add-blog", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title: data.name,
          author: data.author,
          body: data.body,
        }),
      });
      await res.json().then(() => {
        setData({
          name: "",
          author: "",
          body: "",
        });
      });
    } catch (err) {
      console.log(err);
    }

    // const response = await fetch("/blogs", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify({
    //     name: data.name,
    //     author: data.author,
    //     body: data.body,
    //   }),
    // });
    // const dd = await response.json();

    // if (dd.status === 422 || !dd) {
    //   console.log("invalid");
    // } else {
    //   console.log("success");
    // }

    // navigate("/");
  };

  return (
    <div className="container">
      <div className="create">
        <form onSubmit={handleSubmit}>
          <label>Blog Title:</label>
          <input
            id="name"
            type="text"
            value={data.name}
            onChange={(e) => handle(e)}
          />
          <label>Author:</label>
          <input
            id="author"
            type="text"
            value={data.author}
            onChange={(e) => handle(e)}
          />
          <label>Blog Body:</label>
          <textarea
            id="body"
            value={data.body}
            onChange={(e) => handle(e)}
            // required
          />
          <button type="submit">Add Blog</button>
        </form>
      </div>
    </div>
  );
};

export default Create;
